package main

import (
	"fmt"
)

const input = ""

func main() {
	fmt.Printf("Part 1: %d\n", tune(input, 4))
	fmt.Printf("Part 2: %d\n", tune(input, 14))
}

// tune parses a signal rune-by-rune until a number of distinct elements are detected in a row, configured via marker.
// It returns the number of the character after finding the first match.
func tune(signal string, marker int) int {
	seen := map[rune]int{}

	// Sliding window of width ${marker} until we find ${marker} distinct elements within the window
	for i, s := range signal {
		if len(seen) == marker {
			return i
		}

		seen[s]++
		if i < marker {
			continue
		}

		if seen[[]rune(signal)[i-marker]] <= 1 {
			delete(seen, []rune(signal)[i-marker])
		} else {
			seen[[]rune(signal)[i-marker]]--
		}
	}

	// Shouldn't happen
	return -1
}
